const initialState = {
    businesses: []
};

const reducer = (state = initialState, action = {}) => {
    switch (action.type) {
        case 'SET_BUSINESSES': {
            return {
                businesses: action.payload
            }
        }
        default:
            return {
                ...state
            }
    }
}

export default reducer
